mapper.select:
select * from 
(select rownum idx, s.* from
	(select * from trip_board t join city c on t.city_no = c.city_no 
	where t.t_bo_status = #{tBoStatus} 
	order by t.t_bo_no desc) s
)
 where idx >= #{pageStart} and idx <= #{pageEnd}

select * from
(select rownum idx, s.* from
	(select * from trip_board t join city c on t.city_no = c.city_no  
	where t.city_no = #{cityNo} order by t.t_bo_no desc) s
)
 where idx >= #{pageStart} and idx <= #{pageEnd}

    select * from
	(select rownum idx, s.* from
		(select * from trip_board t join city c on t.city_no = c.city_no 
		where t.hashtag = #{hashtag}
		order by t.t_bo_no desc) s
	)
	 where idx >= #{pageStart} and idx <= #{pageEnd}

select * from
(select rownum idx, s.* from
	(select * from trip_board t join city c on t.city_no = c.city_no 
	order by t.t_bo_date asc) s
)
	 where idx >= #{pageStart} and idx <= #{pageEnd}

select * from
(select rownum idx, s.* from
	(select * from trip_board t join city c on t.city_no = c.city_no 
	order by t.t_bo_readcount desc) s
)
 where idx >= #{pageStart} and idx <= #{pageEnd}

select * from
(select rownum idx, s.* from
	(select * from trip_board t join city c on t.city_no = c.city_no order by t.t_bo_no desc) s
)
 where idx >= #{pageStart} and idx <= #{pageEnd}
